onClick:
{{
  insert_receta_ingrediente
    .run({ 
      receta_id:          appsmith.store.receta_activa_id,
      ingrediente_id:     select_ingrediente.selectedOptionValue,
      cantidad_original:  input_cantidad.value,
      unidad_original:    select_unidad.selectedOptionValue,
    })
    .then(() => {
      // Resetea TODOS los campos del formulario:
      resetWidget("select_ingrediente");
      resetWidget("input_cantidad");
      resetWidget("select_unidad");


      // Refresca la tabla de ingredientes:
      return get_receta_ingredientes.run();
    })
    .then(() => {
      showAlert("Ingrediente agregado ðŸ‘", "success");
    })
    .catch(err => {
      showAlert("Error al agregar ingrediente: " + err.message, "error");
    });
}}
